window.onscroll=function(){[...document.querySelectorAll(".detect-top")].forEach(e=>{window.pageYOffset>172?e.classList.add("at-top"):e.classList.remove("at-top")})};const openEls=document.querySelectorAll("[data-open]"),closeEls=document.querySelectorAll("[data-close]"),isVisible="is-visible";var body=document.body;for(const e of openEls)e.addEventListener("click",(function(){const e=this.dataset.open;document.getElementById(e).classList.add(isVisible),body.classList.add("turn-off-scroll")}));for(const e of closeEls)e.addEventListener("click",(function(){this.parentElement.parentElement.parentElement.classList.remove(isVisible),body.classList.remove("turn-off-scroll")}));document.addEventListener("click",e=>{e.target==document.querySelector(".modal.is-visible")&&(document.querySelector(".modal.is-visible").classList.remove(isVisible),body.classList.remove("turn-off-scroll"))}),document.addEventListener("keyup",e=>{"Escape"==e.key&&document.querySelector(".modal.is-visible")&&(document.querySelector(".modal.is-visible").classList.remove(isVisible),body.classList.remove("turn-off-scroll"))});